step.screen:
$ cd /path/to/openidm/
$ echo \"newpwd\" > /tmp/newpwd.json
$ java -jar bundle/json-crypto-cli-.jar
 -encrypt
 -keystore security/keystore.jceks
 -storetype jceks
 -storepass changeit
 -alias "new-sym-key"
 -srcjson /tmp/newpwd.json

