step.screen:
$ curl \
 --cacert self-signed.crt \
 --header "X-OpenIDM-Username: openidm-admin" \
 --header "X-OpenIDM-Password: openidm-admin" \
 --request GET \
 "https://localhost:8443/openidm/managed/user?_queryId=query-all&_fields=userName,_id"
{
    "result" : [ {
       "userName" : "jdoe",
       "_id" : "0918e1ca-7d5e-473d-8b64-97f47cb606f6"
    }, {
       "userName" : "bjensen",
       "_id" : "86fa4740-b250-47ff-af46-515f544b468c"
    } ],
    "resultCount" : 2,
    "pagedResultsCookie" : null,
    "remainingPagedResults" : -1
}

$ curl \
 --cacert self-signed.crt \
 --header "Content-type: application/json" \
 --header "X-OpenIDM-Username: openidm-admin" \
 --header "X-OpenIDM-Password: openidm-admin" \
 --request GET \
 "https://localhost:8443/openidm/managed/role?_queryFilter=true"
{
   "result" : [ {
      "properties" : {
         "name" : "Employee",
         "description" : "Role assigned to workers on the payroll."
      },
      "_id" : "Employee",
      "_rev" : "1"
   }, {
      "properties" : {
         "name" : "Contractor",
         "description" : "Role assigned to contract workers."
      },
      "_id" : "Contractor",
      "_rev" : "1"
   } ],
   "resultCount" : 2,
   "pagedResultsCookie" : null,
   "remainingPagedResults" : -1
}

$ curl \
 --cacert self-signed.crt \
 --header "Content-type: application/json" \
 --header "X-OpenIDM-Username: openidm-admin" \
 --header "X-OpenIDM-Password: openidm-admin" \
 --header "If-Match: *" \
 --request PATCH \
 --data '[
      {
           "operation" : "add",
           "field" : "/assignments",
           "value" : {
                "ldap": {
                     "attributes": [
                          {
                               "name": "employeeType",
                               "value": "Employee",
                               "assignmentOperation" : "mergeWithTarget",
                               "unassignmentOperation" : "removeFromTarget"
                          }
                     ]
                }
           }
      }
 ]' \
 "https://localhost:8443/openidm/managed/role/Employee"

$ curl \
 --cacert self-signed.crt \
 --header "Content-Type: application/json" \
 --header "X-OpenIDM-Username: openidm-admin" \
 --header "X-OpenIDM-Password: openidm-admin" \
 --request GET \
 'https://localhost:8443/openidm/managed/user?_queryFilter=/userName+eq+"bjensen"&_fields=_id,userName,roles,effectiveRoles,effectiveAssignments'

 {
   "result" : [ {
      "_id" : "86fa4740-b250-47ff-af46-515f544b468c",
      "userName" : "bjensen",
      "roles" : [ "openidm-authorized", "managed/role/Employee" ],
      "effectiveRoles" : [ "openidm-authorized", "managed/role/Employee" ],
      "effectiveAssignments" : {
         "ldap" : {
            "attributes" : [ {
               "name" : "employeeType",
               "value" : "Employee",
               "assignmentOperation" : "mergeWithTarget",
               "unassignmentOperation" : "removeFromTarget",
               "assignedThrough" : "managed/role/Employee"
            } ]
         }
      }
   } ],
   "resultCount" : 1,
   "pagedResultsCookie" : null,
   "remainingPagedResults" : -1
}

$ curl \
 --cacert self-signed.crt \
 --header "Content-Type: application/json" \
 --header "X-OpenIDM-Username: openidm-admin" \
 --header "X-OpenIDM-Password: openidm-admin" \
 --header "If-Match: *" \
 --request PATCH \
 --data '[
    {
       "operation" : "add",
           "field" : "/assignments/ldap/attributes/-",
           "value" : {
              "name": "ldapGroups",
              "value": [
                 "cn=Employees,ou=Groups,dc=example,dc=com",
                 "cn=Chat User,ou=Groups,dc=example,dc=com"
              ],
              "assignmentOperation" : "mergeWithTarget",
              "unassignmentOperation" : "removeFromTarget"
           }
       }
    ]' \
"https://localhost:8443/openidm/managed/role/Employee"
{
   "properties" : {
      "name" : "Employee",
      "description" : "Role assigned to workers on the payroll."
   },
   "_id" : "Employee",
   "_rev" : "3",
   "assignments" : {
      "ldap" : {
         "attributes" : [ {
            "name" : "employeeType",
            "value" : "Employee",
            "assignmentOperation" : "mergeWithTarget",
            "unassignmentOperation" : "removeFromTarget"
         }, {
            "name" : "ldapGroups",
            "value" : [ "cn=Employees,ou=Groups,dc=example,dc=com", "cn=Chat User,ou=Groups,dc=example,dc=com" ],
            "assignmentOperation" : "mergeWithTarget",
            "unassignmentOperation" : "removeFromTarget"
         } ]
      }
   }
}

